templateFields.css:
#address-band-right-content {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  height: 100%;
}

#nhs-number {
  text-align: right;
  font-size: var(--small-fontsize);
}

#date {
  text-align: right;
  font-size: var(--small-fontsize);
}

#qr-code {
  width: 20mm;
  height: 20mm;
}

#nhs-logo {
  margin-right: 10mm;
  margin-top: 5mm;
  margin-left: auto;
}

.headed-list ul {
  margin-top: 0.4em;
}

.headed-list p {
  margin-bottom: 0.4em;
}

#data-protection {
  font-size: var(--small-fontsize);
}

strong {
  font-family: 'Noto Sans Bold';
}
